import React from 'react';
import axios from 'axios';
import './Home.scss';
import { GetVideoData, InitializeMVO, GetMainVideoObject } from '../../js/loadSampleData';
import MainVideo from '../MainVideo/MainVideo';
import VideoDesc from '../VideoDesc/VideoDesc';
import NewComment from '../NewComment/NewComment';
import CommentList from '../CommentList/CommentList';
import SideVideoList from '../SideVideoList/SideVideoList';

class Home extends React.Component {
    // *** State Initialization ***
    state = {
        videos: [],
        mainvideo: InitializeMVO(),
        initFlag: false
    };

    // Initialization of data after components are 
    // inserted into the DOM tree
    componentDidMount() {
        console.log('componentDidMount');

        axios.get(window.$BF_URL + window.$BF_VIDEOS + window.$BF_API_KEY)
            .then(result => {
                console.log('result from axios.get', result.data)
                let firstVideo = result.data[0]
                let sideVideos = result.data.splice(1, result.data.length - 1)
                console.log('firstVideo: ', firstVideo)
                console.log('sideVideos: ', sideVideos)
                console.log('initFlag: ', this.state.initFlag);
                if (this.state.initFlag) { return };
                this.setState({ initFlag: true });
            })
            .catch(err => console.log('Error=>', err.response));

        GetVideoData()
            .then(result => {
                let firstVideo = result[0]
                let sideVideos = result.splice(1, result.length - 1)
                this.setState({ videos: sideVideos })
                return GetMainVideoObject(firstVideo)
            })
            .then(result => {
                this.setState({ mainvideo: result })
            })
            .catch(err => console.log('Error=>', err.response));
    };

    componentDidUpdate() {
        console.log('componentDidUpdate');
        console.log('initFlag: ', this.state.initFlag);
    };

    render() {
        return (
            <div>
                <MainVideo mainVideoObj={this.state.mainvideo} />
                <main className="main">
                    <div className="main__left">
                        <VideoDesc mainVideoObj={this.state.mainvideo} />
                        <NewComment />
                        <CommentList videoComments={this.state.mainvideo.comments} />
                    </div>
                    <div className="main__right">
                        <SideVideoList videoList={this.state.videos} />
                    </div>
                </main>
            </div>
        )
    }
}

export default Home;
