import React from 'react';
import './App.scss';
import {GetVideoDataNew, GetVideoData, GetMainVideoObjectNew, GetMainVideoObject} from './js/loadSampleData';
import Header from './components/Header/Header';
import MainVideo from './components/MainVideo/MainVideo';
import VideoDesc from './components/VideoDesc/VideoDesc';
import NewComment from './components/NewComment/NewComment';
import CommentList from './components/CommentList/CommentList';
import SideVideoList from './components/SideVideoList/SideVideoList';

class App extends React.Component {
  // State initialization
  state = {
    // videos: getVideoData(),
    videos: [],
    // mainvideo: GetMainVideoObject() 
    mainvideo: ''
  };

  componentDidMount() {
    console.log('component mounted')
    GetVideoDataNew()
    .then(result => {
      console.log('result',result)
      let mainVideo = result[0]
      console.log('mainVideo',mainVideo)
      let sideVideos = result.splice(1, result.length - 1)
      console.log('sideVideos',sideVideos)
      this.setState({videos: sideVideos})
      return GetMainVideoObjectNew()
    })
    .then(result => {
      console.log('mainvideo',result)
      this.setState({mainvideo: result})
    })
    .catch(err => console.log('Error=>', err.response));
  };

  componentWillUnmount() {
    console.log('component will unmount')
  };

  render() {
    return (
      <div>
        <Header />
        <MainVideo mainVideoObj={this.state.mainvideo}/>
        <main className="main">
          <div className="main-left">
            <VideoDesc mainVideoObj={this.state.mainvideo}/>
            <NewComment />
            <CommentList videoComments={this.state.mainvideo.comments}/>
          </div>
          <div className="main-right">
            <SideVideoList videoList={this.state.videos}/>
          </div>
        </main>
      </div>
    )
  }
}

export default App;
