import React, { Component } from 'react';
import './Comment.scss';

class Comment extends Component {
    render() {
        let {name, date, ts, text} = this.props;

        return (
            <div className="comment" id={key}>
                <div className="comment__image">
                    <img className="comment-image" alt="comment img"/>
                </div>
                <div className="comment__detail">
                    <div className="comment__header">
                        <p className="comment__header-name">{name}</p>
                        <p className="comment__header-ts">{ts}</p>
                    </div>
                    <div className="comment__comments" id={key}>
                        <p className="comment__comments-text">{text}</p>
                    </div>
                </div>
            </div>
        );
    }
}

export default Comment;

