import React from 'react';
import './Comments.scss';

export default function Comments () {
    return (
    <section className="comments">
        <h2 className="comments__title">3 Comments</h2>
        <form className="comments__form" id="comments__form" action="">
            <div className="comments__form-image">
                <img className="comment-image" src="../assets/images/Mohan-muruge-sq.jpg"/>
            </div>
            <div className="comments__form-main">
                <label className="comments__form-label--comment" for="commentText">COMMENT</label>
                <textarea 
                    className="comments__form-textarea" 
                    rows="5" cols="35" 
                    name="commentText" id="commentText" required
                    placeholder="Add a new comment"></textarea> 
                <button className="comments__form-button" id="comments__form-button" type="submit">COMMENT</button>
            </div>
        </form>
    </section>
    );
}
