import React from 'react';
import './App.scss';
import {videoData} from './js/videoData';
import Header from './components/Header/Header';
import MainVideo from './components/MainVideo/MainVideo';
import VideoDesc from './components/VideoDesc/VideoDesc';
import NewComment from './components/NewComment/NewComment';
import CommentList from './components/CommentList/CommentList';
import SideVideoList from './components/SideVideoList/SideVideoList';

class App extends React.Component {
  // State initialization here
  state = {
    videos: [],
    comments: []
  };

  // Functions here
  loadVideos = () => {
    console.log('test')
    if (this.state.videos && this.state.videos.length > 0) {
      console.log('videos array is NOT empty!');
    } else {
      const sideVideos = videoData();
      console.log(sideVideos);
      this.setState({videos: sideVideos});
      console.log(this.state.videos);
    }
  };

  render() {
    return (
      <div className="body">
        {this.loadVideos()}
        <Header />
        <MainVideo />
        <main className="main">
          <div className="main-left">
            <VideoDesc />
            <NewComment />
            <CommentList />
          </div>
          <div className="main-right">
            <hr className="sidevideolist-divider"></hr>
            <SideVideoList />
          </div>
        </main>
      </div>
    )
  }
}

export default App;
